import React, {Component} from 'react';






export default class Checkout extends Component {

    
    openchekout = e =>{
        e.preventDefault();

        var handler = window.ePayco.checkout.configure({
            key: 'f0d5cd55ce4173d87981c04616b99fa8',
            test: false
        });
    let data = {
          name: "Vestido Mujer Primavera",
          description: "Vestido Mujer Primavera",
          invoice: "1234",
          currency: "cop",
          amount: this.props.cartTotal,
          tax_base: this.props.cartSubTotal,
          tax:  this.props.cartTax,
          country: "co",
          lang: "en",
          external: "false",
          extra1: "extra1",
          extra2: "extra2",
          extra3: "extra3",
          confirmation: "http://secure2.payco.co/prueba_curl.php",
          response: "http://payco.co",
          name_billing: this.props.name+" "+this.props.lastname,
          address_billing: "Carrera 19 numero 14 91",
          type_doc_billing: "cc",
          mobilephone_billing: "3050000000",
          number_doc_billing: "100000000",
          email_billing:this.props.email
          }
        handler.open(data);
    }
    render(){
       
              
            

        console.log(this.props)


               
        
	
        return(

                <React.Fragment>
                    
                    <input
                    type="submit"
                    placeholder="pagar"
                    onClick={this.openchekout}
                    /> 
                  
              
                    </React.Fragment>
       
        )
    } 
   
}
